const AccountPageSidebar = (props) => {
  const { openSidebar, setOpenSidebar } = props;
  const pathname = usePathname();

  return (
    <>
      <div
        className={`fixed left-0 top-0 z-[99999] h-screen w-full max-w-[290px] duration-300 xl:static xl:z-[99] xl:h-auto xl:max-h-full ${
          openSidebar ? "translate-x-0" : "-translate-x-full xl:translate-x-0"
        }`}
      >
        <div className="mb-12 h-full lg:mb-0">
          <div className="h-full overflow-hidden overflow-y-auto border-r border-stroke bg-white p-4 shadow-fb-one lg:block">
            <ul className="mt-2 pl-1">
              {links.map((link) => (
                <li key={link.id} className="mb-[6px]">
                  <Link href={`${link.path}`}>
                    <span
                      onClick={() => setOpenSidebar(false)}
                      className={`group flex items-center rounded-lg px-4 py-3 font-heading text-base font-medium capitalize hover:bg-fb-gray hover:text-primary ${
                        pathname.startsWith(link.path)
                          ? "bg-fb-gray text-primary"
                          : "text-body-color"
                      }`}
                    >
                      <span className="pr-2">{link?.icon}</span>
                      {link.name}
                    </span>
                  </Link>
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
      <div
        onClick={() => setOpenSidebar(false)}
        className={`fixed left-0 top-0 z-[999] h-screen w-full bg-fb-black/[.85] duration-300 xl:hidden ${
          openSidebar ? "translate-x-0" : "-translate-x-full"
        }`}
      ></div>
    </>
  );
};

export default AccountPageSidebar;
